sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    console.log("button clicked ..");

    // we need to push the data to table

    const {
        name,
        email,
        amount
    } = api.state;

    if (name && email && amount) {
        const templateFields = "u_email=" + name + "^u_requested_amount=" + amount;

        api.data.create_record_1.execute({
            table: "x_217453_sample_0_sample_crud",
            templateFields: templateFields,
            useSetDisplayValue: false
        });
    }



}
